create table odos_crrs_svc.bldg 
(
  bldg_id serial not null,
  bldg_nm varchar(200) not null,
  bldg_dsc varchar(200),
  primary key (bldg_id)
);
COMMENT ON TABLE odos_crrs_svc.bldg IS 'Represents the conference building'; 
COMMENT ON COLUMN odos_crrs_svc.bldg.equip.bldg_id IS 'Primary key of the conference building'; 
COMMENT ON COLUMN odos_crrs_svc.bldg.equip.bldg_nm IS 'Name of the conference building'; 
COMMENT ON COLUMN odos_crrs_svc.bldg.equip.bldg_dsc IS 'Description of the conference building';
